mapper.select:
SELECT m.name AS mname, a.credt AS credt, a.cnsfeedbk AS cnsfeedbk, f.efeed1 AS efeed1,f.efeed2 AS efeed2,f.pfeed1 AS pfeed1,f.pfeed2 AS pfeed2, f.vfeed1 AS vfeed1,f.vfeed2 AS vfeed2,
    f.sttfeed1 AS sttfeed1, f.sttfeed2 AS sttfeed2, d.question AS question, a.intno AS intno, d.qno AS qno 
    FROM tbmember m JOIN tbintres a ON m.memno = a.memno JOIN tbintdetail d ON a.intno = d.intno JOIN tbintfeedbk f ON a.intno = f.intno WHERE a.intno = #{intno}
